mapper.select:
SELECT COUNT(pay_id) 
FROM haon_pay 
WHERE DATE_FORMAT(pay_date, "%Y-%m-%d") = CURDATE();

      SELECT SUM(pay_price)
	  FROM haon_pay
	  WHERE DATE_FORMAT(pay_date, "%Y-%m-%d") = CURDATE();

      SELECT user_id, MONTH(user_date) FROM haon_user
	  WHERE user_date BETWEEN '2022-10-01' AND NOW()
	  ORDER BY user_date DESC;
